            return helper(nums, lo, hi);
        }
        if (nums[mid] >= nums[lo]) {
            lo = mid;
        } else {
            hi = mid;
        }
    }
    return nums[0];    // 如果数组本身已经是排好序的，第一个数字是最小的数字
}

