public int majorityElement(int[] nums) {
   if (nums == null || nums.length == 0) return 0;
   int n = nums.length;
   Map map = new HashMap<>();

   // 统计数组中每个数字的出现次数
   for (int num : nums) {
       map.put(num, map.getOrDefault(num, 0) + 1);
   }

   // 找到出现次数大于n/2的元素返回
   for (Map.Entry e : map.entrySet()) {
       if (e.getValue() > n / 2) {
           return e.getKey();
       }
   }

   return 0;
}

